<template>
  <div
    class="v-checkbox-group"
    :class="[
      'v-checkbox-group--' + mode,
    ]"
    role="group"
    aria-label="checkbox-group">
    <slot></slot>
  </div>
</template>
<script>
  import Emitter from 'free-vui/src/mixins/emitter';

  export default {
    name: 'CheckboxGroup',
    componentName: 'CheckboxGroup',
    mixins: [Emitter],
    props: {
      value: {
        type: Array,
        default: () => [],
      },
      min: Number,
      max: Number,
      mode: {
        type: String,
        default: 'horizontal',
      },
      size: {
        type: String,
        default: 'small',
      },
      disabled: {
        type: Boolean,
        default: false,
      },
    },
    computed: {
      checkboxGroupSize() {
        return this.size || (this.$VUI || {}).size;
      },
    },
  };
</script>
